import Vue from 'vue'
import Router from 'vue-router'

Vue.use(Router)


// 模块路由(基于主入口布局页面)
export const moduleRoutes = {
    path: '/',
    component: () => import('@/views/main'),
    name: 'main',
    redirect: { name: 'home' },
    meta: { title: '主入口布局' },
    children: [
      { path: '/home', component: () => import('@/views/modules/home'), name: 'home', meta: { title: '首页', isTab: true } }
    ]
  }
